// Create Meteor model
var oModel = new MeteorModel();
this.getView().setModel(oModel);

// Subscribe to Employees data.
this._subscription = Meteor.subscribe('employees');

// Create a json model with data from a file and make it our view model
var oModel = new JSONModel('/webapp/demo/compare/Employees.json');
this.getView().setModel(oModel);

// Set up Odata model for Employees - will be populated via Northwind
// odata service.  We use a proxy due to CORS issues.  Calls to the proxy 
// are redirected to http://services.odata.org/V2/Northwind/Northwind.svc
var oModel = new ODataModel('/oDataProxy');
this.getView().setModel(oModel);
